import BeigeCard from "../../layout/card/BeigeCard";
import { useSelector } from "react-redux";
import "./AllAssociations.scss";
import { Link } from "react-router-dom";
import { useAppDispatch } from "../../../store/store";
import { Fragment, useEffect, useState } from "react";
import { fetchAssociations } from "../../../store/AssociationDataSlice";
import { fetchNewAssociations } from "../../../store/newAssociationsSlice";
import { fetchPopularAssociations } from "../../../store/popularAssociationsSlice";
import { fetchOpenAssociations} from "../../../store/openAssociationsSlice";

const AssociationsGrid = ({ filter }: any) => {
  const [pageNumber, setPageNumber] = useState(1);


  const dispatch = useAppDispatch();
  const { associations } = useSelector((state: any) => state.associations);
  const { newAssociations } = useSelector((state: any) => state.newAssociations);
  const { popularAssociations } = useSelector((state: any) => state.popularAssociations);
  const { openAssociations } = useSelector((state: any) => state.openAssociations);

  useEffect(() => {
    dispatch(fetchAssociations());
    dispatch(fetchNewAssociations());
    dispatch(fetchPopularAssociations());
    dispatch(fetchOpenAssociations());
  }, []);

  let pages = [];
  for (let page = 1; page <= 2; page++) {
    pages.push(
      <Link
        key={page}
        className="page"
        to={`/associations/${filter}/${page}`}
        onClick={() => setPageNumber(page)}
      >
        {page}
      </Link>
    );
  }

  return (
    <Fragment>
      {filter === "popular" && (
        <Fragment>
          <div className="associations-grid">
            {popularAssociations.map((item: any) => (
              <Link
                to={`/associations/${item.name}`}
                className="to-association-btn"
                key={item._id}
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={""}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            ))}
          </div>
        </Fragment>
      )}

      {filter === "all" && pageNumber === 1 && (
        <Fragment>
          <div className="associations-grid">
            {associations.map((item: any) => (
              <Link
                to={`/associations/${item.name}`}
                className="to-association-btn"
                key={item._id}
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={"{item.description}"}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            ))}
          </div>
          <div className="pagination">{pages}</div>
        </Fragment>
      )}

      {filter === "new" && pageNumber === 1 && (
        <Fragment>
          <div className="associations-grid">
            {newAssociations.map((item: any) => (
              <Link
                to={`/associations/${item.name}`}
                className="to-association-btn"
                key={item._id}
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={item.description}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            ))}
          </div>       
        </Fragment>
        )}

{filter === "open" && pageNumber === 1 && (
        <Fragment>
          <div className="associations-grid">
            {openAssociations.map((item: any) => (
              <Link
                to={`/associations/${item.name}`}
                className="to-association-btn"
                key={item._id}
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={item.description}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            ))}
          </div>       
        </Fragment>
        )}
    </Fragment>
  );
};
export default AssociationsGrid;
