import "./VolunteeringPage.scss";
import Input from "../../layout/form/Input";
import line from "../../../assets/line-7-copy-2.png";
import { useSelector } from "react-redux";
import { useAppDispatch } from "../../../store/store";
import { useEffect, useState } from "react";
import { fetchVolunteering } from "../../../store/volunteeringDataSlice";

const VolunteeringPage = (props: { volunteering: string }) => {
  const dispatch = useAppDispatch();
  const { volunteeringStore } = useSelector((state: any) => state.volunteering);

  useEffect(() => {
    dispatch(fetchVolunteering());
  }, []);

  const volunteeringDetails = volunteeringStore.filter((item: any) => item.name === props.volunteering)[0];

  const inputsContainer = (content: string) => {
    return (
      <div>
        <p>{content}</p>
        <img src={line} alt="line" className="line"></img>
      </div>
    );
  };

  const [quantityNumber, setQuantitynumber] = useState(1);
  const [choosenSide, setChoosenSide]: any = useState();

  const chooseSide = (e: any) => {
    e.preventDefault();
  };

  const addVolunteering = () => {
    setQuantitynumber(quantityNumber + 1);
  };
  const removeVolunteering = () => {
    setQuantitynumber(quantityNumber - 1);
  };

  return (
    <div className="volunteering">
      <div className="volunteering-details">
        <img
          src={volunteeringDetails.mobileImage}
          alt="img"
          className="volunteering-page-img"
          srcSet={`${volunteeringDetails.desktopImage} 800w`}
        ></img>
        <div className="data-content">
          <h3 className="volunteering-title">

            {volunteeringDetails.name}
          </h3>
          <p className="volunteering-page-description">{volunteeringDetails.description}</p>
          <br></br>
          <div className="wrapper-volunteering-page">
            <div className="outter"></div>
            <span className="outter-span">
              <span className="ils">₪</span>
              {volunteeringDetails.price}
            </span>
            <div className="outter"></div>
          </div>
        </div>
      </div>
      {inputsContainer("Choose a side")}
        {volunteeringDetails.side.map((side: any) => (
          <Input
            content={side}
            type={"radio"}
            placeholder={""}
            onChange={(e: any) => chooseSide(e)}
            value={""}
            minLength={1}
            maxLength={2}
            key={side}
          />
        ))}
      {inputsContainer("Changes")}
        {volunteeringDetails.changes.map((change: any) => (
          <Input
            content={change}
            type={"checkbox"}
            placeholder={""}
            onChange={""}
            value={""}
            minLength={1}
            maxLength={2}
            key={change}
          />
        ))}
      {inputsContainer("Quantity")}
      <div className="quantity-container">
        <button onClick={removeVolunteering}>-</button>{" "}
        <span className="quantity-number">{quantityNumber}</span>
        <button onClick={addVolunteering}>+</button>
      </div>
    </div>
  );
};

export default VolunteeringPage;
