import React, { Fragment, useEffect, useState } from 'react'
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchVolunteering } from '../../../store/volunteeringDataSlice';
import { useAppDispatch } from '../../../store/store';
import BeigeCard from '../../layout/card/BeigeCard';

const VolunteeringGrid = () => {
    const [pageNumber, setPageNumber] = useState(1);

    const dispatch = useAppDispatch();
    const { volunteering } = useSelector((state: any) => state.founders);

    useEffect(() => {
      dispatch(fetchVolunteering());
    }, []);

    const numberOfPages = Math.ceil(founders.length / 6);
    let pages = [];
    for (let page = 1; page <= numberOfPages; page++) {
      pages.push(
        <Link
          key={page}
          className="page"
          to={`/founders/${page}`}
          onClick={() => setPageNumber(page)}
        >
          {page}
        </Link>
      );
    }

    return (
      <Fragment>
        <div className="associations-grid founders-grid">
          {founders.slice(pageNumber * 6 - 6, pageNumber * 6).map((item: any) => (
            <Link to={`/founder/${item.founderName}`} className="to-founder-btn" key={item._id}>
              <BeigeCard
                key={item.founderName}
                title={item.founderName}
                detail={""}
                img={item.founderImage}
              ></BeigeCard>
            </Link>
          ))}
        </div>
        <div className="pagination">{pages}</div>
      </Fragment>
    );
}

export default VolunteeringGrid
