import { Fragment } from "react";
import "./HomePage.scss";
import { Link } from "react-router-dom";

const FounderItem = ({ founder }:any) => {
  return (
    <Fragment>
      {founder.map((founderDetails:any) => {
        if (founderDetails.founderOfTheWeek === true) {
          return (
            <div className="founder-container" key={founderDetails._id}>
              <div className="founder-img">
                <img
                  src={founderDetails.founderImage}
                  alt="founder"
                  className="founder-img"
                ></img>
                <h1>{founderDetails.founderName}</h1>
              </div>
              <p className="founder-description">{founderDetails.founderDescription}</p>
            </div>
          );
        }
      })}
    </Fragment>
  );
};

export default FounderItem;
