import BeigeCard from "../../layout/card/BeigeCard";
import { useSelector } from "react-redux";
import "./AllRestaurants.scss";
import { Link } from "react-router-dom";
import { useAppDispatch } from "../../../store/store";
import { Fragment, useEffect, useState } from "react";
import { fetchAssociations } from "../../../store/AssociationDataSlice";
import { fetchNewAssociations } from "../../../store/newAssociationsSlice";
import { fetchPopularAssociations } from "../../../store/popularAssociationsSlice";
import { fetchOpenAssociations} from "../../../store/openAssociationsSlice";

const RestaurantsGrid = ({ filter }: any) => {
  const [pageNumber, setPageNumber] = useState(1);


  const dispatch = useAppDispatch();
  const { restaurants } = useSelector((state: any) => state.restaurants);
  const { newAssociations } = useSelector((state: any) => state.newRestaurants);
  const { popularAssociations } = useSelector((state: any) => state.popularRestaurants);
  const { openAssociations } = useSelector((state: any) => state.openRestaurants);

  useEffect(() => {
    dispatch(fetchAssociations());
    dispatch(fetchNewAssociations());
    dispatch(fetchPopularAssociations());
    dispatch(fetchOpenAssociations());
  }, []);

  let pages = [];
  for (let page = 1; page <= 2; page++) {
    pages.push(
      <Link
        key={page}
        className="page"
        to={`/associations/${filter}/${page}`}
        onClick={() => setPageNumber(page)}
      >
        {page}
      </Link>
    );
  }
  return (
    <Fragment>
      {filter === "popular" && (
        <Fragment>
          <div className="restaurants-grid">
            {popularAssociations.map((item: any) => (
              <Link
                to={`/associations/${item.name}`}
                className="to-restaurant-btn"
                key={item._id}
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={item.chef}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            ))}
          </div>
        </Fragment>
      )}

      {filter === "all" && pageNumber === 1 && (
        <Fragment>
          <div className="restaurants-grid">
            {restaurants.map((item: any) => (
              <Link
                to={`/associations/${item.name}`}
                className="to-restaurant-btn"
                key={item._id}
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={item.chef}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            ))}
          </div>
          <div className="pagination">{pages}</div>
        </Fragment>
      )}

      {filter === "new" && pageNumber === 1 && (
        <Fragment>
          <div className="restaurants-grid">
            {newAssociations.map((item: any) => (
              <Link
                to={`/associations/${item.name}`}
                className="to-restaurant-btn"
                key={item._id}
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={item.chef}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            ))}
          </div>       
        </Fragment>
        )}

{filter === "open" && pageNumber === 1 && (
        <Fragment>
          <div className="restaurants-grid">
            {openAssociations.map((item: any) => (
              <Link
                to={`/associations/${item.name}`}
                className="to-restaurant-btn"
                key={item._id}
              >
                <BeigeCard
                  key={item.name}
                  title={item.name}
                  detail={item.chef}
                  img={item.mobileImage}
                ></BeigeCard>
              </Link>
            ))}
          </div>       
        </Fragment>
        )}
    </Fragment>
  );
};
export default RestaurantsGrid;
