import { Fragment } from "react";
import "./HomePage.scss";
import { Link } from "react-router-dom";

const FounderItem = ({ founder }:any) => {
  return (
    <Fragment>
      {founder.map((founderDetails:any) => {
        if (founderDetails.founderOfTheWeek === true) {
          return (
            <div className="founder-container" key={founderDetails._id}>
              <div className="founder-img">
                <img
                  src={founderDetails.founderImage}
                  alt="founder"
                  className="founder-img"
                ></img>
                <h1>{founderDetails.founderName}</h1>
              </div>
              <p className="founder-description">{founderDetails.founderDescription}</p>
            </div>
          );
        }
      })}
      <br />
      <div className="chefs-restaurants">
        <p className="chef-left-title">yossiâ€™s restaurants :</p>
        <div className="chef-restaurants-container">
          {restaurants.map((restaurant:any) => {
            if (restaurant.chef === "Yossi Shitrit") {
              return (
                <Link
                  to={`/restaurant/${restaurant.name}`}
                  className="to-restaurant" key={restaurant._id}
                >
                  <div className="chef-restaurant-item">
                    <img
                      src={restaurant.mobileImage}
                      alt="img"
                      className="chef-restaurant-img"
                    />{" "}
                    <h3>{restaurant.name}</h3>
                  </div>
                </Link>
              );
            }
          })}
        </div>
      </div>
    </Fragment>
  );
};

export default FounderItem;
