import clock from "../../../assets/clock-icon.svg";
import DishSmallCard from "../../layout/card/VolunteeringSmallCard";
import "./AssociationPage.scss";
import V from "../volunteeringPage/VolunteeringPage";
import { useParams } from "react-router-dom";
import Footer from "../../footer/Footer";
import Header from "../../header/Header";
import { useSelector } from "react-redux";
import { useAppDispatch } from "../../../store/store";
import { useEffect, Fragment, useState } from "react";
import { fetchAssociations } from "../../../store/AssociationDataSlice";
import { fetchVolunteering } from "../../../store/volunteeringDataSlice";

const AssociationPage = () => {
  const [openDishCard, setOpenDishCard] = useState(false);
  const [dishName, setDishName] = useState("");

  const dispatch = useAppDispatch();
  const { associations } = useSelector((state: any) => state.associations);
  const { dishes } = useSelector((state: any) => state.dishes);

  useEffect(() => {
    dispatch(fetchAssociations());
    dispatch(fetchVolunteering());
  }, []);

  const openDishCardHandler = (dish: string) => {
    setOpenDishCard(true);
    setDishName(dish);
  };

  const params: any = useParams();

  const associationDetails = associations.filter(
    (item: any) => item.name === params.associationName
  );

  const associationDishes = dishes.filter(
    (item: any) => item.association === params.associationName
  );

  const mealsTypes: string[] = ["all", "Breakfast", "Lunch", "Dinner"];
  const [filterType, setFilterType] = useState("all");

  const filteredArr = associationDishes;

  const opening = associationDetails[0].openingHour.split(":")[0] * 1;
  const closing = associationDetails[0].closingHour.split(":")[0] * 1;
  const now = new Date().getHours();
  const ifOpen: boolean = now >= opening && now <= closing;
  return (
    <div className="association-page">
      <Header />
      {openDishCard && (
        <Fragment>
          <button
            className="dish-container"
            onClick={() => setOpenDishCard(false)}
          ></button>
          <button
            className="close-dish-page"
            onClick={() => setOpenDishCard(false)}
          >
            X
          </button>
          <br />
          <DishPage dish={dishName}></DishPage>
        </Fragment>
      )}
      {!openDishCard && (
        <div className="association-page-container">
          <img
            src={associationDetails[0].desktopImage}
            alt="association"
            className="association-page-img"
            srcSet={`${associationDetails[0].bigImage} 800w`}
          ></img>
          <h1>{params.associationName}</h1>
          <h2>{associationDetails[0].description}</h2>
          {ifOpen && (
            <div>
              <img src={clock} alt="clock"></img> <span>Open now</span>
            </div>
          )}

          <div className="dishes-container">
            {filteredArr.map((dish: any) => (
              <button
                className="to-dish-btn"
                onClick={() => openDishCardHandler(dish.name)}
                key={dish._id}
              >
                <div className="association-item">
                  <DishSmallCard
                    img={dish.mobileImage}
                    signatureDesktopImage={dish.desktopImage}
                    title={dish.name}
                    detail={dish.description}
                    price={dish.price}
                    key={dish.key}
                  ></DishSmallCard>
                </div>
              </button>
            ))}
          </div>
          <Footer />
        </div>
      )}
    </div>
  );
};

export default AssociationPage;
